jQuery.fn.tabsPro=function(a){var a=jQuery.extend({classTabPassive:"tab",classTabActive:"tab-active",classTabContent:"tab-content",activeTab:1,showErrors:true},a);return this.each(function(){if(jQuery("."+a.classTabActive).size()==0){var b=1;var c=window.location.href.split("#");if(c.length==2){jQuery("."+a.classTabPassive).each(function(){if(jQuery(this).attr("href")=="#"+c[1]){jQuery(this).addClass(a.classTabActive);jQuery("#"+jQuery(this).attr("rel")).css("display","block");}else{jQuery("#"+jQuery(this).attr("rel")).css("display","none");}});}else{jQuery("."+a.classTabPassive).each(function(){if(a.activeTab==b){jQuery(this).addClass(a.classTabActive);jQuery("#"+jQuery(this).attr("rel")).css("display","block");}else{jQuery("#"+jQuery(this).attr("rel")).css("display","none");}++b;});}}jQuery(this).find("."+a.classTabPassive).click(function(){jQuery("."+a.classTabActive).toggleClass(a.classTabActive);jQuery(this).toggleClass(a.classTabActive);jQuery("."+a.classTabContent).css("display","none");jQuery("#"+jQuery(this).attr("rel")).css("display","block");if(typeof jQuery("#"+jQuery(this).attr("rel")).attr("id")=="undefined"&&a.showErrors===true){alert('Вкладка с идентификатором "'+jQuery(this).attr("rel")+'" не найдена!');}return true;});});};